import { IoFemale, IoMale, IoMaleFemale } from 'react-icons/io5';
import './GenderFilter.css';

const GenderFilter = ({ groupType, setGroupType }) => {
  const options = [
    { value: 'all', label: 'ALL', icon: <IoMaleFemale /> },
    { value: 'gg', label: 'GG', icon: <IoFemale /> },
    { value: 'bg', label: 'BG', icon: <IoMale /> }
  ];

  const handleOptionClick = (value) => {
    setGroupType(value);
  };

  return (
    <div className="gender-filter-container">
      {options.map(option => (
        <div
          key={option.value}
          className={`gender-filter-item ${groupType === option.value ? 'selected' : ''}`}
          onClick={() => handleOptionClick(option.value)}
        >
          {option.icon}
          <span className="gender-filter-label">{option.label}</span>
        </div>
      ))}
    </div>
  );
};

export default GenderFilter;
