  const [numClicks, setNumClicks] = useState(0);
  const [dvHistory, setDvHistory] = useState([]);
  const [currIndex, setCurrIndex] = useState(-1);

  useEffect(() => {
    console.log('State Updated:', dvData);
  }, [dvData]);


  return (
