import "./GenderFilter.css";
import React, { forwardRef } from 'react';

const GenderFilter = forwardRef((props, ref) => (
  <label className="gender-filter-container">
    <input type="checkbox" className="hidden" ref={ref} {...props} />
    <div className={`toggle-switch ${props.checked ? "checked" : ""}`}>
      <div className={`toggle-thumb ${props.checked ? "checked-thumb" : ""}`}>
        {props.checked ? <icon>✔️</icon> : <icon>❌</icon>}
      </div>
    </div>
  </label>
));

export default GenderFilter;
