import React, { useState } from 'react';
import './GenderFilter.css';

const GenderFilter = ({ groupType, setGroupType }) => {
  const [isOpen, setIsOpen] = useState(false);

  const options = [
    { value: 'all', label: 'All' },
    { value: 'gg', label: 'Girl Group' },
    { value: 'bg', label: 'Boy Group' }
  ];

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionClick = (value) => {
    setGroupType(value);
    setIsOpen(false);
  };

  return (
    <div className="gender-filter-container">
      <div className="dropdown-header" onClick={handleToggle}>
        {options.find(option => option.value === groupType)?.label || 'Select...'}
      </div>
      {isOpen && (
        <ul className="dropdown-list">
          {options.map(option => (
            <li
              key={option.value}
              className="dropdown-list-item"
              onClick={() => handleOptionClick(option.value)}
            >
              {option.label}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default GenderFilter;
