import React, { useState } from 'react';
import { IoFemale, IoMale, IoMaleFemale, IoFilter } from 'react-icons/io5';
import './GenderFilter.css';

const GenderFilter = ({ groupType, setGroupType }) => {
  const [isVisible, setIsVisible] = useState(false);


  const options = [
    { value: 'all', label: 'ALL', icon: <IoMaleFemale /> },
    { value: 'gg', label: 'GG', icon: <IoFemale /> },
    { value: 'bg', label: 'BG', icon: <IoMale /> }
  ];

  const handleOptionClick = (value) => {
    setGroupType(value);
    setIsVisible(false);
  };

  const toggleVisibility = () => {
    setIsVisible(!isVisible);
  };

  return (
    <div className="gender-filter-container">
      {/* Arrow and label to toggle the visibility of the filter bar */}
      <div className="gender-filter-toggle" onClick={toggleVisibility}>
        <IoFilter />
        <span>Gender Filters</span>
      </div>

      {/* The gender filter bar, which appears when isVisible is true */}
      {isVisible && (
       <div className={`gender-filter-bar ${isVisible ? 'visible' : ''}`}>
          {options.map(option => (
            <div
              key={option.value}
              className={`gender-filter-item ${groupType === option.value ? 'selected' : ''}`}
              onClick={() => handleOptionClick(option.value)}
            >
              {option.icon}
              <span className="gender-filter-label">{option.label}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default GenderFilter;;
